require(["gitbook", "jquery"], function(gitbook, $) {

    var load = function(){

        var doc =  $('#api-doc');

        if(doc.length > 0) {
            // do this only for our page
            $("div.page-inner").css('max-width', '80%');
        }
    }
    $(document).ready(load);

    gitbook.events.bind("page.change", load);

});